var hash = window.location.hash.substring(1);
var params = {}
hash.split('&').map(hk => { 
  let temp = hk.split('='); 
    params[temp[0]] = temp[1] 
});

if(params.id_token && params.access_token && params.expires_in && params.token_type == "Bearer"){
    $('#loader').fadeIn(200);
    var auth = {};
    auth.AccessToken = params.access_token;
    auth.IdToken = params.id_token;
    auth.ExpiresIn = params.expires_in;
    auth.TokenType = params.token_type;
    auth.timeAcquired = new Date().getTime();
    window.localStorage.setItem('Auth',JSON.stringify(auth));
    var xhttp = new XMLHttpRequest();
    xhttp.onloadend = function(){
        $('#loader').fadeOut(200);
        if(this.status == 200 && JSON.parse(this.responseText).statusCode == 200){
            let result = SON.parse(this.responseText)
            let auth = JSON.parse(window.localStorage.getItem('Auth'));
            auth.username = result.body.username;
            auth.isAdmin = result.body.isAdmin;
            window.localStorage.setItem('Auth',JSON.stringify(auth));
            window.location.href = ("index.html");
        }
        else if(JSON.parse(this.responseText).errorType){
            $('#notification').html(JSON.parse(this.responseText).errorMessage);
        }
        else{
            console.log(this);
        }
    }
    xhttp.open("GET",`https://v7gmuisen3.execute-api.ap-southeast-1.amazonaws.com/beta/googlelogin?accesstoken=${JSON.parse(window.localStorage.getItem("Auth")).AccessToken}`,true);
    xhttp.setRequestHeader("token", JSON.parse(window.localStorage.getItem("Auth")).IdToken);
    xhttp.send();
}

